mapper.delete:

update t_invite_info 
set status=1
where id = #{id,jdbcType=INTEGER}

mapper.select:

select t_invite_info.id, invite_openId,t_wx_user.nickname, invite_day, invite_begin, invite_end, invite_address, create_time
from t_invite_info join t_wx_user on t_invite_info.invite_openId=t_wx_user.openId
where t_invite_info.id = #{id,jdbcType=INTEGER}
